import React, { useContext, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AppContext, IAppContext } from "../../context/AppContext";
import "./style.css";

export default function Home() {
  const navigate = useNavigate();
  const formRef = useRef(null);
  const { state, dispatch } = useContext(AppContext);
  const { socket, uid } = state;

  console.log("first", state);

  const onEnter = () => {
    // TODO: currently roomId is 1745
    // socket.emit('enterRoom', {userName:}, 1745)
  };

  const onClick = (e: React.SyntheticEvent) => {
    e.preventDefault();
    // navigate("/room");
    const target = e.target as typeof e.target & {
      userName: { value: string };
    };
    const userName = target.userName.value;
    if (userName.trim() === "") {
      console.log("无效用户名");
      return;
    }
    console.log(userName, "dfdf");
    if (uid === null) {
      socket.emit("createUser", userName);
      console.log("only once")
    }
    navigate("/room");
  };

  useEffect(() => {
    socket.on("getUserInfo", (player) => {
      console.log("getUserInfo", player);
      dispatch({type: "update_info", payload: player})
    });
  }, []);

  return (
    <>
      <div className="container">
        <form className="login-form" ref={formRef} onSubmit={onClick}>
          <h2>欢迎登录</h2>
          <input type="text" name="userName" placeholder="请随意输入玩家名称" />
          {/* <input name="roomId" placeholder="请输入房间号" /> */}
          <button type="submit">加入</button>
        </form>
      </div>
    </>
  );
}
